mapper.update:
update auth_user
set balance = #{balanceUpdateDTO.totalAmount}
where id = #{balanceUpdateDTO.userId}

mapper.select:
select au.id As userId, au.client_id As username, au.create_time As createTime, au.user_url As userUrl from auth_user au WHERE 1 = 1
AND au.client_id LIKE CONCAT(#{userListDTO.keyword}, '%')
order by au.create_time desc LIMIT #{page.page}, #{page.rows}
